import { Card, Popover, Button } from "antd";
import { Content } from "antd/lib/layout/layout";
import React from "react";
import PropTypes from "prop-types";
import {
  RetweetOutlined,
  HeartTwoTone,
  HeartOutlined,
  MessageOutlined,
  EllipsisOutlined,
} from "@ant-design/icons/lib/icons/RetweetOutlined";
import { useSelector } from "react-redux";

// 기획 후 코딩
const PostCard = ({ post }) => {
  const { me } = useSelector((state) => state.user.me?.id);     // ?. 옵셔널 체이닝 연산자
  return (
    <div>
      <Card
        cover={post.Image[0] && <PostImage Images={post.Image} />}
        actions={[
          <RetweetOutlined key="retweet" />,
          <HeartOutlined key="heart" />,
          <MessageOutlined key="comment" />,
          <Popover
            key="more"
            content={
              <Button.Group>
                {id && post.User.id === id && (
                    <>
                    <Button>수정</Button>
                <Button type="danger">삭제</Button>
                    </>
                )}
                <Button>신고</Button>
              </Button.Group>
            }>
            <EllipsisOutlined />
          </Popover>,
        ]}>
        <Image />
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

PostCard.propTypes = {
  post: PropTypes.shape({
      id: PropTypes.number,
      User: PropTypes.object,
      content: PropTypes.string,
      createAt: PropTypes.object,
      Comments: PropTypes.arrayOf(PropTypes.object),        // 객체들의 배열 
      Images: PropTypes.arrayOf(PropTypes.object),        // 객체들의 배열 
  }).isRequired,
};

export default PostCard;
