import React from "react";
import { useSelection } from "react-redux";
import AppLayout from "../components/AppLayout";

const Home = () => {
  const { isLoggedIn } = useSelection((state) => state.user);
  const { mainPosts } = useSelection((state) => state.post);
  return (
    <AppLayout>
      {isLoggedIn && <PostForm />}
      {mainPosts.map((post, index) => (
        <PostCard key={index} post={post} />
      ))}
    </AppLayout>
  );
};

export default Home;
