import { Card, Popover } from "antd";
import { Content } from "antd/lib/layout/layout";
import React from "react";
import {RetweetOutlined, HeartOutlined ,MessageOutlined } from "@ant-design/icons/lib/icons/RetweetOutlined"

// 기획 후 코딩
const PostCard = ({ post }) => {
  return (
    <div>
      <Card cover={post.Image[0] && <PostImage Images={post.Image} />}
      actions={[
          <RetweetOutlined />,
          <HeartOutlined />,
          <MessageOutlined />,
          <Popover>
              <EllipsisOutlined />
          </Popover>
      ]}
      >
        <Image />
        <Content />
      </Card>
      <CommentForm />
      <Comments />
    </div>
  );
};

export default PostCard;
